select x.p from (select distinct a.city as p from (select left (city, 1) as one, city from station) as a where a.one = 'A' or a.one = 'E' or a.one = 'I' or a.one = 'O' or a.one = 'U' order by a.city) as x, (select distinct b.city as q from (select right (city, 1) as one, city from station) as b where b.one = 'A' or b.one = 'E' or b.one = 'I' or b.one = 'O' or b.one = 'U' order by b.city) as y where x.p = y.q;
